    private void btnGuardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGuardarActionPerformed
        try {
            
            //Recolectar datos de Reporte
             String idReporte = txtIdReporte.getText();
             String fechaTexto = txtFechaTexto.getText();
             String tipo = cmbTipoReporte.getSelectedItem().toString();
             String zonaReporte = txtZonaReporte.getText();
            
             // Crear cliente
             Cliente cliente = new Cliente (
                txtNombreCompleto.getText(),
                txtIdReportante.getText(),
                txtTelefonoContacto.getText()  
             );
             
             //Crear mascota (Perro o gato)
             Mascota mascota = new Perro(
                    "DOG",
                    txtColorPrincipal.getText(),
                    txtSeniasParticulares.getText(),
                    txtMicrochip.getText(),
                    txtNombre.getText(),
                    txtRaza.getText(),
                    cmbTalla.getSelectedItem().toString(),
                    chkTieneCollar.isSelected()
            );
                 
                
            //Crear reporte segun tipo
            ReporteMascota reporte;
                if ("PDR".equals(tipo)){
                    reporte = new ReportePerdida(idReporte, fechaTexto, false, zonaReporte, cliente, mascota);

                }else{
                    reporte = new ReporteEncontrada(idReporte, fechaTexto, false, zonaReporte, cliente, mascota);                    

                }

            //5. Guardar usando GestorArchivos
            GestorArchivos gestor = new GestorArchivos();
                gestor.guardarReporte(reporte);
            //6.Confirmacion al usuario
                JOptionPane.showMessageDialog(this, "Reporte guardado exitosamente!");
                
            //7. CERRAR formulario y volver al menú
                this.dispose();
                new VentanaPrincipal().setVisible(true);

        } catch (IllegalArgumentException e) {
                JOptionPane.showMessageDialog(this, "Error de validación:\n" + e.getMessage(), "Error", JOptionPane.ERROR_MESSAGE);
                
        } catch (Exception e){
            JOptionPane.showMessageDialog(this, "Error inesperado: \n" + e.getMessage(), "Error", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_btnGuardarActionPerformed

