    private void txtNombreFocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_txtNombreFocusLost
        String texto = txtNombre.getText().trim();
    
        // CAMPO OPCIONAL - si está vacío, NO debe marcar error
        if (texto.isEmpty()) {
            txtNombre.setBackground(Color.WHITE);  
            lblErrorNombre.setText("");  
            return;
        }

        try {
            // Solo valida si NO está vacío
            Mascota mascotaTest = new Perro();
            mascotaTest.setNombre(texto);  // ← ¡Ojo! Es el nombre de la mascota

            txtNombre.setBackground(Color.WHITE);
            lblErrorNombre.setText("Nombre válido");
            lblErrorNombre.setForeground(Color.BLUE);

        } catch (IllegalArgumentException e) {
            txtNombre.setBackground(new Color(255, 200, 200));
            lblErrorNombre.setText("Error " + e.getMessage());
            lblErrorNombre.setForeground(Color.RED);
        }
    }//GEN-LAST:event_txtNombreFocusLost

